import React, { useState } from 'react';
import './recipePage.css';

export default function RecipePage({ name, desc, image, ingredients, steps}) {

  const [newIngredient, setNewIngredient] = React.useState('');
  const [newInstruction, setNewInstruction] = React.useState('');

  const addIngredient = () => {
    console.log(newIngredient)
    var ul = document.getElementById("ingredientList");
    var li = document.createElement("li");
    li.appendChild(document.createTextNode(newIngredient));
    ul.appendChild(li);
  }
  const addInstruction = () => {
    console.log(newInstruction)
    var ul = document.getElementById("instructionList");
    var li = document.createElement("li");
    li.appendChild(document.createTextNode(newInstruction));
    ul.appendChild(li);
  }

  return (
    <div>
      <section>
        <div className="flex-container">
          <div className="flex-header">
            <h2 id="recipeName">{name}</h2>
            <p id="recipeDescription">{desc}</p>
          </div>
          <img id="recipeImage" className="flex-image" src={image} />
        </div>
      </section>
      <section >
        <h3>Ingredients</h3>
        <ul id="ingredientList">
          {ingredients.map(function(name, index){
            return <li key={index}>{name}</li>;
          })}
        </ul>
        <div className="form-group"> 
          <h4> Add an Ingredient</h4>
            <label>Ingredient: </label>
            <input 
              id="newIngredient" 
              className="form-element" 
              placeholder="2 cups of spinach" 
              value={newIngredient}
              onChange={(e) => {
                setNewIngredient(e.target.value);
              }}
            /> <br /> <br />
          <button className="form-button" onClick={addIngredient}>Add Ingredient</button>
        </div>
        </section>
        <section>
        <h3>Instructions</h3>
        <ol id="instructionList">
          {steps.map(function(name, index){
            return <li key={index}>{name}</li>;
          })}
        </ol>
        <div className="form-group"> 
          <h4> Add an Instruction</h4>
            <div className="vertical-align">
              <label>Ingredient: </label>
              <input 
                id="newInstruction" 
                className="form-element" 
                placeholder="Pour and Serve!" 
                value={newInstruction}
                onChange={(e) => {
                  setNewInstruction(e.target.value);
                }}
              /> <br /> <br />
            </div>
          <button className="form-button" onClick={addInstruction}>Add Instruction</button>
        </div>
      </section>
    </div>
  );
}
